import { useContext } from "react";
import { Link } from "react-router-dom";
import { CategoryExercise } from "../components/CategoryExercise";
import { ErrorMessage } from "../components/ErrorMessage";
import { ExercisesList } from "../components/ExercisesList";
import { AuthContext } from "../context/AuthContext";
import { useExercises } from "../hooks/useExercises";

export const HomePage = () => {
  const { exercises, loading, error, removeExercise, filterExercises } =
    useExercises("");
  const { categories } = useCategories();
  const { user } = useContext(AuthContext);

  if (loading) return <p>Cargando ejercicios...</p>;
  if (error) return <ErrorMessage message={error} />;

  return (
    <section>
      {user !== null && user.admin === 1 ? (
        <Link to={"/exercises"}>
          <button>Add new exercise</button>
        </Link>
      ) : null}
      <CategoryExercise
        filterExercises={filterExercises}
        categories={categories}
      />
      <h1>Lista de Ejercicios</h1>
      <ExercisesList exercises={exercises} removeExercise={removeExercise} />
    </section>
  );
};
