import { useState } from "react";
import Select, { InputActionMeta } from "react-select";
export const CategoryExercise = ({ filterExercises, categories }) => {
  const [error, setError] = useState("");
  const [selected, setSelected] = useState("");

  const handleChange = async (e) => {
    console.log("handleChange", e);
    try {
      let value = e.target.value;
      setSelected(value);
      console.log("selected", selected);
      filterExercises(value);
    } catch (error) {
      setError(error.message);
    }
  };

  const onInputChange = (inputValue, { action, prevInputValue }) => {
    console.log("action", action);
    console.log("inputValue", inputValue);
    if (action === "input-change") {
      setSelected(inputValue);
      console.log("selected", inputValue);
      filterExercises(inputValue);
      return inputValue;
    }
    if (action === "menu-close") {
      if (prevInputValue) {
        setSelected(inputValue);
        filterExercises(inputValue);
      }
      //else setMenuIsOpen(undefined);
    }
    return prevInputValue;
  };

  return (
    <form>
      <label>
        Pick your favorite category:
        <Select
          onInputChange={onInputChange}
          defaultValue={selected}
          options={categories}
        />
      </label>
    </form>
  );
};
