import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { editExerciseService } from "../services";

export const EditExercise = ({ id, exercise }) => {
  const [error, setError] = useState("");
  const [sending, setSending] = useState(false);
  const { token } = useContext(AuthContext);
  const [exerciseInfo, setExerciseInfo] = useState({
    name: exercise.name,
    category: exercise.category,
    img: "",
    description: exercise.description,
  });
  const handleChange = (e) => {
    setExerciseInfo((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleForm = async (e) => {
    e.preventDefault();

    try {
      setSending(true);

      const data = new FormData(e.target);
      const [name, category, img, description] = data.values();
      await editExerciseService({
        id,
        name,
        category,
        img,
        description,
        token,
      });
      //e.target.reset();
    } catch {
      setError(error.message);
    } finally {
      setSending(false);
    }
  };

  return (
    <form onSubmit={handleForm}>
      <h1>Edit exercise</h1>
      <fieldset>
        <label htmlFor="text">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={exerciseInfo.name}
          required
          onChange={handleChange}
        />
      </fieldset>
      <fieldset>
        <label htmlFor="text">Category:</label>
        <input
          type="text"
          id="category"
          name="category"
          value={exerciseInfo.category}
          required
          onChange={handleChange}
        />
      </fieldset>
      <fieldset>
        <label htmlFor="image">Image</label>
        <input type="file" id="img" name="img" accept="image/*" />
      </fieldset>
      <fieldset>
        <label htmlFor="text">Description:</label>
        <input
          type="text"
          id="description"
          name="description"
          value={exerciseInfo.description}
          required
          onChange={handleChange}
        />
      </fieldset>
      <button>Upload</button>
      {sending ? <p>Uploading exercise...</p> : null}
      {error ? <p>{error}</p> : null}
    </form>
  );
};
