import { useContext } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { useExercise } from "../hooks/useExercise";
import { deleteExerciseService } from "../services";
import { ErrorMessage } from "./ErrorMessage";

export const exercise = () => {
  const { id } = useParams();
  const { user, token } = useContext(AuthContext);
  const navigate = useNavigate();

  const { exercise, loading, error, setError } = useExercise(id);

  if (loading) return <p>Cargando ejercicios...</p>;
  if (error) return <ErrorMessage message={error} />;

  const deleteExercise = async (id) => {
    try {
      await deleteExerciseService({ id, token });
      navigate("/");
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <article>
      <p>
        <Link to={`/exercises/${exercise.id}`}>{exercise.name}</Link>
        <p>{exercise.category}</p>
      </p>
      {exercise.img ? (
        <img
          alt=""
          src={`${process.env.REACT_APP_BACKEND}/uploads/${exercise.img}`}
        ></img>
      ) : null}

      <p>{exercise.description}</p>

      <section>
        {user !== null && user.admin === 1 ? (
          <>
            <Link to={"/exercises"}>
              <button>Edit exercise</button>
            </Link>
            <Link to={"/exercises"}>
              <button onClick={() => deleteExercise(exercise.id)}>
                Delete exercise
              </button>
              {error ? <p>{error}</p> : null}
            </Link>
          </>
        ) : null}
      </section>
    </article>
  );
};
