import { useContext } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { ErrorMessage } from "../components/ErrorMessage";
import { Exercise } from "../components/Exercise";
import { AuthContext } from "../context/AuthContext";
import { useExercise } from "../hooks/useExercise";
import { deleteExerciseService } from "../services";

export const ExercisePage = () => {
  const { id } = useParams();
  const { user, token } = useContext(AuthContext);
  const navigate = useNavigate();

  const { exercise, loading, error, setError } = useExercise(id);

  if (loading) return <p>Cargando ejercicios...</p>;
  if (error) return <ErrorMessage message={error} />;

  const deleteExercise = async (id) => {
    try {
      await deleteExerciseService({ id, token });
      navigate("/");
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <section>
      <h1>Ejercicio:</h1>
      <Exercise exercise={exercise} />

      {user !== null && user.admin === 1 ? (
        <>
          <Link to={"/exercises"}>
            <button>Edit exercise</button>
          </Link>
          <Link to={"/exercises"}>
            <button onClick={() => deleteExercise(exercise.id)}>
              Delete exercise
            </button>
            {error ? <p>{error}</p> : null}
          </Link>
        </>
      ) : null}
    </section>
  );
};
