import { useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { deleteExerciseService } from "../services";

export const Exercise = ({ exercise }) => {
  const { user } = useContext(AuthContext);
  return (
    <article>
      <p>
        <Link to={`/exercises/${exercise.id}/details`}>{exercise.name}</Link>
        <p>{exercise.category}</p>
      </p>
      {exercise.img ? (
        <img
          alt=""
          src={`${process.env.REACT_APP_BACKEND}/uploads/${exercise.img}`}
        ></img>
      ) : null}

      <p>{exercise.description}</p>

      {user !== null && user.admin === 1 ? (
        <>
          <Link to={`/exercises/${exercise.id}/edit`} state={exercise}>
            <button>Edit exercise</button>
          </Link>
          <Link to={"/exercises"}>
            <button onClick={() => deleteExerciseService(exercise.id)}>
              Delete exercise
            </button>
            {error ? <p>{error}</p> : null}
          </Link>
        </>
      ) : null}
    </article>
  );
};
