import { useEffect, useState } from "react";
import { getAllExercisesService } from "../services";

export const useExercises = (category) => {
  const [exercises, setExercises] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const loadExercises = async (category) => {
    try {
      setLoading(true);

      const data = await getAllExercisesService(category);
      setExercises(data);
      console.log("no setCategories");
      if (category === undefined) {
        console.log("setCategories");
        let categoriesNames = data.map((e) => e.category);
        categoriesNames = categoriesNames.filter(
          (c, index) => categoriesNames.findIndex((c2) => c2 === c) === index
        );
        setCategories(categoriesNames);
      }
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    console.log("loadExercises");
    loadExercises();
  }, []);

  /*   //añadimos el ejercicio nuevo a la lista de ejercicios
  const addExercise = (exercise) => {
    setExercises([...exercises, exercise]);
  }; */
  //actualizamos el estado con los todos los ejercicios con id diferente a la del ejercicio borrado
  const removeExercise = (id) => {
    setExercises(exercises.filter((exercise) => exercise.id !== id));
  };

  const filterExercises = (category) => {
    loadExercises(category);
  };

  return {
    exercises,
    categories,
    loading,
    error,
    removeExercise,
    filterExercises,
  };
};
