//función para registrarse
export const registerUserService = async ({ name, email, password }) => {
  const response = await fetch(`${process.env.REACT_APP_BACKEND}/user`, {
    method: "POST",
    body: JSON.stringify({ name, email, password }),
    headers: {
      "Content-Type": "application/json",
    },
  });

  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
};

// función para loguearse
export const loginUserService = async ({ email, password }) => {
  const response = await fetch(`${process.env.REACT_APP_BACKEND}/login`, {
    method: "POST",
    body: JSON.stringify({ email, password }),
    headers: {
      "Content-Type": "application/json",
    },
  });
  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};

// función para obtener información del usuario logueado
export const getMyUserDataService = async ({ token }) => {
  const response = await fetch(`${process.env.REACT_APP_BACKEND}/user`, {
    method: "GET",
    headers: {
      Authorization: token,
    },
  });
  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};

// función para leer los datos de cualquier usuario
export const getUserDataService = async ({ id, token }) => {
  const response = await fetch(`${process.env.REACT_APP_BACKEND}/users/${id}`, {
    method: "GET",
    headers: {
      Authorization: token,
    },
  });

  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};

// función para obtener todos los usuarios
export const getAllUsersService = async ({ token }) => {
  const response = await fetch(`${process.env.REACT_APP_BACKEND}/users`, {
    method: "GET",
    headers: {
      Authorization: token,
    },
  });

  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};

//funcioón para editar usuario
export const editUserService = async ({ id, email, name, token }) => {
  const response = await fetch(`${process.env.REACT_APP_BACKEND}/users/${id}`, {
    method: "PUT",
    body: JSON.stringify({ email, name }),
    headers: {
      Authorization: token,
      "Content-Type": "application/json",
    },
  });
  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};

//función para eliminar un usuario
export const deleteUserService = async ({ id, token }) => {
  const response = await fetch(`${process.env.REACT_APP_BACKEND}/users/${id}`, {
    method: "DELETE",
    headers: {
      Authorization: token,
      "Content-Type": "application/json",
    },
  });
  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
};

//petición al backend de un ejercicio
export const singleExerciseService = async ({ id, token }) => {
  const response = await fetch(
    `${process.env.REACT_APP_BACKEND}/exercises/${id}`,
    {
      method: "GET",
      headers: {
        Authorization: token,
        "Content-Type": "application/json",
      },
    }
  );

  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};
//petición al backend de todos los ejercicios
export const getAllExercisesService = async ({ category = "", token }) => {
  const response = await fetch(
    `${process.env.REACT_APP_BACKEND}/exercises?category=${category}`,
    {
      method: "GET",
      headers: {
        Authorization: token,
        "Content-Type": "application/json",
      },
    }
  );

  const json = await response.json();
  if (!response.ok) {
    throw new Error(json.message);
  }

  return json.data;
};

// función que se encarga de enviar el formulario de nuevo ejercicio
export const sendNewExerciseService = async ({ data, token }) => {
  const response = await fetch(`${process.env.REACT_APP_BACKEND}/exercises`, {
    method: "POST",
    body: data,
    headers: {
      Authorization: token,
    },
  });
  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};

// función que se encarga de enviar el formulario de editar ejercicio
export const editExerciseService = async ({
  id,
  name,
  category,
  img,
  description,
  token,
}) => {
  const response = await fetch(
    `${process.env.REACT_APP_BACKEND}/exercises/${id}`,
    {
      method: "PUT",
      body: JSON.stringify({ name, category, img, description }),
      headers: {
        Authorization: token,
        "Content-Type": "application/json",
      },
    }
  );
  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};

export const editExerciseService2 = async ({ id, data, image, token }) => {
  const [name, category, img, description] = data.values();
  console.log("name", name);
  const formData = new FormData();
  formData.append("name", name);
  formData.append("category", category);
  formData.append("img", img);
  formData.append("description", description);
  formData.append("img", image);
  //console.log("data", data);
  const response = await fetch(
    `${process.env.REACT_APP_BACKEND}/exercises/${id}`,
    {
      method: "PUT",
      body: formData,
      headers: {
        Authorization: token,
        Accept: "application/json",
        "Content-Type": "multipart/form-data",
      },
    }
  );
  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
  return json.data;
};

// función para borrar un ejercicio
export const deleteExerciseService = async ({ id, token }) => {
  const response = await fetch(
    `${process.env.REACT_APP_BACKEND}/exercises/${id}`,
    {
      method: "DELETE",
      headers: {
        Authorization: token,
      },
    }
  );
  const json = await response.json();

  if (!response.ok) {
    throw new Error(json.message);
  }
};

