import { useState, useEffect } from "react";
export const CategoryExercise = ({ filterExercises, categories }) => {
  const [error, setError] = useState("");
  const [selected, setSelected] = useState("");
  let auxSelected = "";
  useEffect(() => {
    console.log("useEffect auxSelected", auxSelected);
    setSelected(auxSelected);
  }, [auxSelected]);

  const handleChange = (e) => {
    try {
      let value = e.target.value;
      auxSelected = value;

      console.log("value", value);
      console.log("auxSelected", auxSelected);
      filterExercises(value);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <form>
      <label>
        Pick your favorite category:
        <select value={selected} onChange={handleChange}>
          <option key="empty" value="">
            Todos
          </option>
          {categories.map((category) => (
            <option key={category.value} value={category.value}>
              {category.label}
            </option>
          ))}
        </select>
      </label>
    </form>
  );
};
