import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { editExerciseService } from "../services";

export const EditExercise = ({ id, exercise }) => {
  const [error, setError] = useState("");
  const [sending, setSending] = useState(false);
  const { token } = useContext(AuthContext);

  const handleForm = async (e) => {
    e.preventDefault();
    const data = new FormData(e.target);
    const [name, category, img, description] = data.values();
    console.log("category", category);
    console.log("e.target", e.target.value);
    await editExerciseService({ id, name, category, img, description, token });
    /*try {
      setSending(true);

      const data = new FormData(e.target);


      e.target.reset();
    } catch {
      setError(error.message);
    } finally {
      setSending(false);
    }*/
  };

  return (
    <form onSubmit={handleForm}>
      <h1>Edit exercise</h1>
      <fieldset>
        <label htmlFor="text">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={exercise.name}
          required
        />
      </fieldset>
      <fieldset>
        <label htmlFor="text">Category:</label>
        <input
          type="text"
          id="category"
          name="category"
          value={exercise.category}
          required
        />
      </fieldset>
      <fieldset>
        <label htmlFor="image">Image</label>
        <input type="file" id="img" name="img" accept="image/*" />
      </fieldset>
      <fieldset>
        <label htmlFor="text">Description:</label>
        <input
          type="text"
          id="description"
          name="description"
          value={exercise.description}
          required
        />
      </fieldset>
      <button>Upload</button>
      {sending ? <p>Uploading exercise...</p> : null}
      {error ? <p>{error}</p> : null}
    </form>
  );
};
